package 조건문;

public class ifElse테스트2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String data = "ok";
		
		if (data.equals("ok")) {
			System.out.println("긍정");
		} else if ("no") {
			System.out.println("부정");
		} else {
			System.out.println("잘 모르겠음.");
		}
	}

}
